import React, { useState } from 'react'

export default function Login() {

  const[user,setUser]=useState({email:"",password:""});
const Change=(e)=>{
  setUser({...user,[e.target.name]:e.target.value})
}
  const hendlesubmit=async(e)=>{
    e.preventDefault();
    const responce= await fetch(`http://localhost:5000/api/auth/login`,{
      method:"POST",
      headers:{
        'Content-type':'Application/json'
      },
      body: JSON.stringify({email:user.email,password:user.password})
    })
    const json=await responce.json()
    if(json.success){
      localStorage.setItem('token',json.authtoken)
    }
  }
  return (
    <form className='container mx-5 my-4'onSubmit={hendlesubmit}>
    <div className="mb-3">
      <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
      <input type="email" className="form-control" id="email" aria-describedby="emailHelp" name='email' value={user.email} onChange={Change} required/>
      <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
    </div>
    <div className="mb-3">
      <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
      <input type="password" className="form-control" id="exampleInputPassword1" name='password'value={user.password}onChange={Change} required/>
    </div>
    
    <button type="submit" className="btn btn-primary">Login</button>
  </form>
  )
}
