import React, { useContext, useEffect, useState } from 'react'
import mycontext from './Context/Createcontext'
function Selectdata() {
  const {Note, Getnotes}=useContext(mycontext)
  const [data,setData]=useState("10-02-2024");
  const [end,setEnd]=useState("10-02-2024");
  //Call Use Effect Hook To refresh The Data
  useEffect(()=>{
    Getnotes()
  },[])
  const datechange=(e)=>{
    const input=e.target.value
   setData(input.toString())
  };
  const last=(e)=>{
    const input=e.target.value
     setEnd(input.toString())}
  // Date Arry Convert

  const Date=Note.map((note)=>{return note.date});
  const Datearry=parseInt(Date)
  console.log(Datearry)
     //part dates;

  let INputPart=data.split("-");
  const Startdate=`${INputPart[2]}-${INputPart[1]}-${INputPart[0]}`
  // console.log("startdate",INputPart)


  // console.log("start",typeof(data))
  // PART:2 
  let EndPart=end.split("-");
  const Enddate=`${EndPart[2]}-${EndPart[1]}-${EndPart[0]}`
  
  // console.log("end",typeof(end))
// Now Help To Filter Method To Show Result Duriing Time Period;
const Result=Note.filter((note)=>{
  const Notedate=note.date
return  Notedate >= Startdate && Enddate <=Enddate
})
// console.log("myresult",Result)
  return (
    <div>
  <label for="month">Month:</label>
<select id="month" name="month" onChange={datechange}>
  <option value="01">January</option>
  <option value="02">February</option>
  <option value="03">March</option>
  <option value="04">April</option>
  <option value="05">May</option>
  <option value="06">June</option>
  <option value="07">July</option>
  <option value="08">August</option>
  <option value="09">September</option>
  <option value="10">October</option>
  <option value="11">November</option>
  <option value="12">December</option>
</select>

<label for="year">Year:</label>
<input type="number" id="year" name="year" min="1900" max="9999" onChange={datechange}></input>

    </div>
  )
}

export default Selectdata
