export default function Alerts(props) {
  return (
    <div  className='myalert' style={{height:'4rem'}}>
      <div > 
     {props.Alert&& <div className="alert alert-success  myal" role="alert">
  <strong>{props.Alert.type}</strong> : {props.Alert.msg}
</div>}
    </div>
    </div>
  )
}
