import React, { useContext, useState } from 'react'
import mycontext from './Context/Createcontext'
function Selectdata() {
  const {Note}=useContext(mycontext)
  const [data,setData]=useState();
  const [end,setEnd]=useState()
  const datechange=(e)=>{
    const input=e.target.value
   setData(input.toString())
  };
  const last=(e)=>{const input=e.target.value
     setEnd(input.toString())}
     //part dates;

  let INputPart=data.split("-");
  const formate=`${INputPart[2]}-${INputPart[1]}-${INputPart[0]}`
  console.log("start",formate)
  console.log("start",typeof(data))
  // PART:2 
  let EndPart=end.split("-");
  const endFormate=`${EndPart[2]}-${EndPart[1]}-${EndPart[0]}`
  console.log("end",endFormate)
  console.log("end",typeof(end))

  return (
    <div>
      <input type="date"  className='mx-2'  onChange={datechange} value={data} name='start'/>
      <input type="date" onChange={last} name='end' value={end} />
      {data},{end}
    </div>
  )
}

export default Selectdata
