import React, { useContext, useEffect, useState } from 'react'
import diomond from './diomond.png'
import { Link, useNavigate } from 'react-router-dom'
import total from './total.png'
import mycontext from './Context/Createcontext'
function Navbar() {
  const navigate=useNavigate()
  const {Note}=useContext(mycontext)
  useEffect(()=>{

    var sumOfwork = Note.reduce((sum, currentItem) => sum + currentItem.dailywork, 0);
  },[])
  const[sum,setSum]=useState(sumOfwork)
  
const hendleLogout=()=>{
  localStorage.removeItem('token')
  setSum(0)
navigate('/login')
}
  return (
    <nav className="navbar bg-body-tertiary">
  <div className="container-fluid">
    <Link className="navbar-brand" to="/">
      <img src={diomond} alt="Logo" width="40" height="35"className="d-inline-block align-text-top mx-2  "/>
      <i>HIRADAYRI</i>
    </Link>
    <div className='d-flex '>
      <img src={total} width='60' height={60} alt="" />
      <h3 className='mt-2 mx-2' style={{color:'black',textAlign:'center'}}>{sum}</h3>
    </div>
  {!localStorage.getItem('token')?
  <form className="d-flex">
      <Link to='/login' className="btn btn-outline-success mx-2" type="submit">Login</Link>
      <Link to='/sinup' className="btn btn-outline-success mx-2" type="submit">Sinup</Link>
    </form>:<button type="button" className="btn btn-info" onClick={hendleLogout}>Logout</button>}
    </div>
</nav>
  )
}

export default Navbar
