import React, { useContext } from 'react'
import mycontext from '../Context/Createcontext'
import daily from './Daily png.png'
import dicon from './delete.png'
import eicon from  './edit.png'
import Alertcontext from '../Context/AlertContext'
function Showuserdata(props) {
  const {Checkalert}=useContext(Alertcontext)
  const {DeleteNote}=useContext(mycontext)
  const {note,UpdateNote}=props;
  const Delete=()=>{
    if ("Notification" in window) {
      // Request permission to show notifications
      Notification.requestPermission().then(function (permission) {
        if (permission === "granted") {
          // Create a notification
          var notification = new Notification("Your Title", {
            body: "Your notification message here",
          });
    
          // After a certain time (e.g., 5 seconds), close the notification
          setTimeout(function () {
            notification.close();
          }, 5000); // 5000 milliseconds (5 seconds)
        }
      });
    }
    DeleteNote(note._id); Checkalert('Your Work Note Successfully Deleted','Success')
  }
  return (
<> 
<div className='d-flex justify-content-between newcard my-1'>
<p className=" " onClick={Delete}><img className='daily' src={dicon} alt=''/></p>
<span>{note.date}</span>
<span>Price : {note.price}</span>
<span>Work : {note.work}</span>
<span><img src={daily} alt="" className='daily' />{note.dailywork}</span>
<p className=" "onClick={()=>{UpdateNote(note);}}><img className='daily' src={eicon} alt=''/></p>
</div>
    </>
  )
}

export default Showuserdata
