import { useState } from "react";
import mycontext from "./Createcontext";

const updatecontext=(props)=>{
    const mydata=[]
    const [note,setNote]=useState(mydata)
    //fetch data
    const getnotes= async()=>{
        let data= await fetch(`http://localh/ost:5000api/data/mydata`);
        setNote(data)
    }
return (
<mycontext.Provider value={{note,getnotes}} >
    {props.children}
</mycontext.Provider>
);
}

export default updatecontext;
