import React, { useEffect } from 'react'
import mycontext from './Context/Createcontext';
function TryalMonth() {
    const {Note ,Getnotes}={mycontext}
    useEffect(()=>{
        Getnotes()
    },[])
    const date=new Date()
    const month=date.getMonth()+1;
    const months=month.toString()
    const year=date.getFullYear();
    const Currentdate =`${year}-${months.padStart(2,'0')}`;
    function formate(stringdate){
        const [month,year]=stringdate.split('/')
        return `${year}-${month}`
    }
    const result =Note.filter((note)=>{
        const notedate=formate(note.date);
        return Currentdate ==notedate
    })
    console.log(result)

  return (
    <div>
      
    </div>
  )
}

export default TryalMonth
