import React, { useContext, useState } from 'react'
import mycontext from './Context/Createcontext'
function Inputform() {
  const {AddNote}=useContext(mycontext)
  const [data,setData]=useState({price:"",work:""});
const Change=(e)=>{
  setData({...data,[e.target.name]:e.target.value});
}
const hendlesubmit=(e)=>{
  e.preventDefault()
  AddNote(data.price,data.work)
  setData({price:"",work:""});
}

  return (
    <div className='myform'>
      <div  className="mb-3 ">
  <label htmlFor="formGroupExampleInput"  className="form-label">Price</label>
  <input type="number"  className="form-control" id="formGroupExampleInput"name='price' value={data.price} onChange={Change} placeholder="Enter a Single Pice Diomond Price"/>
</div>
<div  className="mb-3">
  <label htmlFor="formGroupExampleInput2"  className="form-label">Made Diomond</label>
  <input type="number"  className="form-control" id="formGroupExampleInput2" name='work' value={data.work} onChange={Change} placeholder="Total Made Diomonds"/>
</div>
<button type="button" className="btn btn-primary" onClick={hendlesubmit}>Submit</button>
    </div>
  )
}

export default Inputform
