import React, { useContext, useEffect } from 'react'
import Inputform from './Inputform'
import Showuserdata from './Usedata/Showuserdata'
import mycontext from './Context/Createcontext'
function Home() {
  const {Note,Getnotes}=useContext(mycontext)
   useEffect(()=>{
    Getnotes()
   },[])
   console.log(Note)

   const sumArrayValues = Note.map(obj => ({
    id: obj._id,
    sum: obj.dailywork.reduce((acc, val) => acc + val, 0)
  }));
  console.log(sumArrayValues)
  return (
    <div  className='container'>
    <div  className='text-center my-2'>
        <h2>HIRADAYRI</h2>
    <p><i>Add Your Daily Work.In This App And Store your work and See you any Time in Work..!</i></p> </div>
    <p>Total work is </p>
      <Inputform/>
      <div className='row'>
        {Note.map((note)=>{
          return(
            <div className='col-md-3' key={note._id}> <Showuserdata note={note}/></div>
          )
        })}
      </div>
      
    </div>
  )
}

export default Home
