import React, { useContext} from 'react'
import diomond from './diomond.png'
import { Link, useNavigate } from 'react-router-dom'
import total from './total.png'
import mycontext from './Context/Createcontext'
function Navbar() {
  const navigate=useNavigate()
  const {Note}=useContext(mycontext)

  //Monthly Total income

  const date=new Date()
  const month=date.getMonth()+1;
  const months=month.toString()
  const year=date.getFullYear();
  const Currentdate =`${year}-${months.padStart(2,'0')}`;
  console.log("Currentdate",Currentdate)
  function formate(stringdate){
      const [day,month,year]=stringdate.split('/')
      return `${year}-${month}`
  }
  // Seprate Month Vise Data
  const result= Note.filter((item) => {
      const itemDate = formate(item.date);
      console.log("notedate",itemDate)
      return Currentdate===itemDate
    });
    // show total Monthly Income

    let sumOfwork = result.reduce((sum, currentItem) => sum + currentItem.dailywork, 0); 
  
const hendleLogout=()=>{
  const logout=window.confirm("Do You Want to Logout..?")
  if(logout){
    localStorage.removeItem('token')
    navigate('/login')
  }

}
  return (
    <nav className="navbar mynav">
  <div className="container-fluid">
    <Link className="navbar-brand d-flex logo" to="/">
      <img src={diomond} alt="Logo" width="50" height="50"className="d-inline-block align-text-top mx-2 "/>
      <i className='heding'><h1>HIRADAYRI</h1></i>
    </Link>
    {localStorage.getItem('token')?<div className='d-flex monneybox'>
      <img src={total} width='60' height={60} alt="" />
      <h3 className='mt-2 monney '>{sumOfwork} </h3>
    </div>: " "}
  {!localStorage.getItem('token')?
  <form className="d-flex">
      <Link to='/login' className="btn btn-outline-success mx-2" type="submit">Login</Link>
      <Link to='/sinup' className="btn btn-outline-success mx-2" type="submit">Sinup</Link>
    </form>:<button type="button" className="btn btn-info" onClick={hendleLogout}>Logout</button>}
    </div>
</nav>
  )
}

export default Navbar
